//import { ADD_MOVIE, DELETE_MOVIE } from '../actions/movieActions.js';
import { movies } from "./../movies";

const initialState = {
    movies:movies,
    currentIndex: 0
  };

const reducer = (state = initialState, action) => {
    switch (action.type) {
      case ADD_FAVORITE:
        const movieToAdd = state.movies[state.currentIndex];
        if (movieToAdd && !state.favorites.find((movie) => movie.id === movieToAdd.id)) {
          return {
            ...state,
            favorites: [...state.favorites, movieToAdd],
          };
        }
        return state;

      case REMOVE_FAVORITE:
        return {
          ...state,
          favorites: state.favorites.filter((movie) => movie.id !== action.payload),
        };

      case NEXT_MOVIE:
        return {
          ...state,
          currentIndex: (state.currentIndex + 1) % state.movies.length,
        };

      default:
        return state;
    }
  };



export default reducer;
