import { ADD_FAVORITE, NEXT_MOVIE, REMOVE_FAVORITE } from '../actions/movieActions.js';
import { movies } from "./../movies";

const initialState = {
    movies: movies,
    sira: 0,
    favorites:[]
  };

const reducer = (state = initialState, action) => {
    switch (action.type) {
      case ADD_FAVORITE:
        const movieToAdd = state.movies[state.currentIndex];
        if (movieToAdd && !state.favorites.find((movie) => movie.id === movieToAdd.id)) {
          return {
            ...state,
            favorites: [...state.favorites, movieToAdd],
          };
        }
        return state;
        case NEXT_MOVIE:
          return {
            ...state,
            currentIndex: (state.sira + 1) % state.movies.length,
          };
          case REMOVE_FAVORITE:
            return {
              ...state,
              favorites: state.favorites.filter((movie) => movie.id !== action.payload),
            };
      default:
        return state;
    }
  };



export default reducer;
