import { useDispatch, useSelector} from 'react-redux';
import { removeFavorite } from '../actions/movieActions.js';
import { moviesOriginal } from "./../movies";

export default function FavMovie({ title, id }) {

  const movies = useSelector(store => store.movies);
  const favMovies = useSelector(store => store.favorites);
  const dispatch = useDispatch();

  function listemdenCikar() {
    dispatch(removeFavorite(id)); // Favorilerden çıkar eylemini tetikle

    const movieIndex = favMovies.findIndex(movie => movie.id === id); // Çıkarılan filmin indexini bul

    const updatedMovies = [...movies]; // Mevcut filmleri güncellenmiş bir diziye kopyala
    const removedMovie = updatedMovies.splice(movieIndex, 1)[0]; // Filmi çıkar ve çıkarılan filmi al

    const insertIndex = movies.findIndex(movie => movie.id > removedMovie.id); // Çıkarılan filmi yerleştireceğiniz indexi bulun

    updatedMovies.splice(insertIndex, 0, removedMovie); // Çıkarılan filmi doğru sıraya yerleştirin

    // Redux'e güncellenmiş filmler listesini gönder

  }
  return (
    <div className="flex p-3 pl-4 bg-white mb-2 shadow items-center group">
      <div className="pr-4 flex-1">{title}</div>
      <button onClick={listemdenCikar} className="px-3 py-2 block text-sm rounded bg-rose-700 text-white opacity-30 group-hover:opacity-100">
        Çıkar
      </button>
    </div>
  );
}
