import { useDispatch, useSelector} from 'react-redux';
import { removeFavorite  } from '../actions/movieActions.js';

export default function FavMovie({ title, id }) {
  const dispatch = useDispatch();
  const favMovies = useSelector(store => store.movies.favorites).map(movie=>movie)

  function handleRemoveFavorite(id) {
    dispatch(removeFavorite(id));
  }
  return (
    <div className="flex p-3 pl-4 bg-white mb-2 shadow items-center group">
      <div className="pr-4 flex-1">{title}</div>
      <button onClick={() => handleRemoveFavorite(favMovies.id)}  className="px-3 py-2 block text-sm rounded bg-rose-700 text-white opacity-30 group-hover:opacity-100">
        Çıkar
      </button>
    </div>
  );
}
